mapper.update:
UPDATE tb_user
SET
    points = COALESCE(points, 0) + #{points},
    account = COALESCE(account, 0) + #{account},
    use_points = COALESCE(use_points, 0) + #{usePoints}
WHERE user_id = #{userId}

UPDATE tb_user
SET account = account - #{userPayAmount}
WHERE user_id = #{userId}

mapper.select:
SELECT account
FROM tb_user AS user
WHERE user_id = #{userId}
